mapper.select:
select com01_corp_cd as com01CorpCd , com01_corp_nm as com01CorpNm , com01_corp_type as com01CorpType , com01_corp_enm as com01CorpEnm , com01_extn_corp_cd as com01ExtnCorpCd
from com01_corp
where com01_corp_cd = #{com01CorpCd}

mapper.update:
update com01_corp 

set  com01_corp_cd = #{com01CorpCd}
,	com01_corp_nm = #{com01CorpNm}
,	com01_corp_type =  #{com01CorpType}
,	com01_corp_enm = #{com01CorpEnm}
,	com01_extn_corp_cd = #{com01ExtnCorpCd}
where com01_corp_cd = #{com01CorpCd}
