mapper.select:
SELECT sys01_user_id as sys01UserId , sys01_user_nm as sys01UserNm , sys01_pwd as sys01Pwd , sys01_user_tel as sys01UserTel , sys01_user_email as sys01UserEmail FROM sys01_user WHERE 1=1
ORDER by sys01_user_nm
OFFSET ${pageAttr.offset} LIMIT ${pageAttr.limit}

mapper.update:
      UPDATE  
      	    sys01_user 
      SET
	        sys01_user_nm    = #{sys01UserNm}
	      , sys01_pwd     = #{sys01Pwd}

      WHERE 1=1
	     AND sys01_user_id = #{sys01UserId}
